.center-horizontal,section{text-align:center}.full-screen,.pin,.scene,body,html{height:100%}body,html{margin:0}h1{font-size:35px}h1,h2,h3,h4,h5,h6{font-family:Oswald,sans-serif;margin:0;padding:0;color:#000}div,li,p,td,th,tr,ul{font-family:Khula,sans-serif;margin:0;padding:0;color:#000}.scene{padding:50px}.inside,.pin{padding:20px}.pin{background:#00f;color:#fff}.inside{background:green}header{position:fixed;top:10px;left:10px}section{color:#EFEFEF}.margin-top-div{margin:20px 20px 0}.margin-div{margin:0 20px}.blue{background-color:#3883d8}.red{background-color:#cf3535}.orange{background-color:#ea6300}.gold{background-color:#eaea20}.dropbtn{background-color:#333;color:#fff;padding:16px;font-size:16px;border:none}.dropdown{position:fixed;float:left;left:0;display:inline-block}.dropdown-content{display:none;position:absolute;background-color:#f1f1f1;min-width:160px;box-shadow:0 8px 16px 0 rgba(0,0,0,.2);z-index:1}.dropdown-content a{color:#000;padding:12px 16px;text-decoration:none;display:block}.dropdown-content a:hover{background-color:#ddd}.dropdown:hover .dropdown-content{display:block}.dropdown:hover .dropbtn{background-color:#ddd;color:#333}